}

import { getDatabase } from './connection';
import { runMigrations } from './migrations/index';

/**
 * Initialize all database tables
 * Creates tables if they don't exist and runs migrations
 */
export function initializeTables() {
  const db = getDatabase();

  // Create goals table
  db.exec(`
    CREATE TABLE IF NOT EXISTS goals (
      id TEXT PRIMARY KEY,
      project_id TEXT NOT NULL,
      context_id TEXT,
      order_index INTEGER NOT NULL,
      title TEXT NOT NULL,
      description TEXT,
      status TEXT NOT NULL CHECK (status IN ('open', 'in_progress', 'done', 'rejected', 'undecided')),
      created_at TEXT NOT NULL DEFAULT (datetime('now')),
      updated_at TEXT NOT NULL DEFAULT (datetime('now')),
      FOREIGN KEY (context_id) REFERENCES contexts(id) ON DELETE SET NULL
    );
  `);

  // Create context_groups table
  db.exec(`
    CREATE TABLE IF NOT EXISTS context_groups (
      id TEXT PRIMARY KEY,
      project_id TEXT NOT NULL,
      name TEXT NOT NULL,
      color TEXT NOT NULL,
      position INTEGER NOT NULL,
      created_at TEXT NOT NULL DEFAULT (datetime('now')),
      updated_at TEXT NOT NULL DEFAULT (datetime('now'))
    );
  `);

  // Create contexts table
  db.exec(`
    CREATE TABLE IF NOT EXISTS contexts (
      id TEXT PRIMARY KEY,
      project_id TEXT NOT NULL,
      group_id TEXT, -- Optional group assignment
      name TEXT NOT NULL,
      description TEXT,
      file_paths TEXT NOT NULL, -- JSON string of file paths array
      has_context_file INTEGER DEFAULT 0, -- Boolean flag for context file existence
      context_file_path TEXT, -- Path to the context file
      preview TEXT, -- Optional preview image path (relative to public folder)
      created_at TEXT NOT NULL DEFAULT (datetime('now')),
      updated_at TEXT NOT NULL DEFAULT (datetime('now')),
      FOREIGN KEY (group_id) REFERENCES context_groups(id) ON DELETE SET NULL
    );
  `);

  // Create events table
  db.exec(`
    CREATE TABLE IF NOT EXISTS events (
      id TEXT PRIMARY KEY,
      project_id TEXT NOT NULL,
      title TEXT NOT NULL,
      description TEXT NOT NULL,
      type TEXT NOT NULL CHECK (type IN ('info', 'warning', 'error', 'success', 'proposal_accepted', 'proposal_rejected')),
      agent TEXT,
      message TEXT,
      created_at TEXT NOT NULL DEFAULT (datetime('now'))
    );
  `);

  // Create scans table with token tracking
  db.exec(`
    CREATE TABLE IF NOT EXISTS scans (
      id TEXT PRIMARY KEY,
      project_id TEXT NOT NULL,
      scan_type TEXT NOT NULL,
      timestamp TEXT NOT NULL DEFAULT (datetime('now')),
      summary TEXT,
      input_tokens INTEGER, -- LLM input tokens used
      output_tokens INTEGER, -- LLM output tokens used
      created_at TEXT NOT NULL DEFAULT (datetime('now'))
    );
  `);

  // Create ideas table
  db.exec(`
    CREATE TABLE IF NOT EXISTS ideas (
      id TEXT PRIMARY KEY,
      scan_id TEXT NOT NULL,
      project_id TEXT NOT NULL,
      context_id TEXT,
      scan_type TEXT DEFAULT 'overall',
      category TEXT NOT NULL,
      title TEXT NOT NULL,
      description TEXT,
      reasoning TEXT,
      status TEXT NOT NULL DEFAULT 'pending' CHECK (status IN ('pending', 'accepted', 'rejected', 'implemented')),
      user_feedback TEXT,
      user_pattern INTEGER DEFAULT 0,
      effort INTEGER CHECK (effort IS NULL OR (effort >= 1 AND effort <= 3)),
      impact INTEGER CHECK (impact IS NULL OR (impact >= 1 AND impact <= 3)),
      created_at TEXT NOT NULL DEFAULT (datetime('now')),
      updated_at TEXT NOT NULL DEFAULT (datetime('now')),
      FOREIGN KEY (scan_id) REFERENCES scans(id) ON DELETE CASCADE,
      FOREIGN KEY (context_id) REFERENCES contexts(id) ON DELETE SET NULL
    );
  `);

  // Create backlog_items table
  db.exec(`
    CREATE TABLE IF NOT EXISTS backlog_items (
      id TEXT PRIMARY KEY,
      project_id TEXT NOT NULL,
      goal_id TEXT,
      agent TEXT NOT NULL CHECK (agent IN ('developer', 'mastermind', 'tester', 'artist', 'custom')),
      title TEXT NOT NULL,
      description TEXT NOT NULL,
      status TEXT NOT NULL CHECK (status IN ('pending', 'accepted', 'rejected', 'in_progress')),
      type TEXT NOT NULL CHECK (type IN ('proposal', 'custom')),
      impacted_files TEXT, -- JSON string of ImpactedFile[]
      created_at TEXT NOT NULL DEFAULT (datetime('now')),
      updated_at TEXT NOT NULL DEFAULT (datetime('now')),
      accepted_at TEXT,
      rejected_at TEXT,
      FOREIGN KEY (goal_id) REFERENCES goals(id) ON DELETE SET NULL
    );
  `);

  // Create implementation_log table
  db.exec(`
    CREATE TABLE IF NOT EXISTS implementation_log (
      id TEXT PRIMARY KEY,
      project_id TEXT NOT NULL,
      requirement_name TEXT NOT NULL,
      title TEXT NOT NULL,
      overview TEXT NOT NULL,
      tested INTEGER DEFAULT 0,
      created_at TEXT NOT NULL DEFAULT (datetime('now'))
    );
  `);

  // Create feature_requests table
  db.exec(`
    CREATE TABLE IF NOT EXISTS feature_requests (
      id TEXT PRIMARY KEY,
      project_id TEXT NOT NULL,
      requester_name TEXT NOT NULL,
      requester_email TEXT,
      source TEXT NOT NULL CHECK (source IN ('ui', 'notion', 'jira', 'confluence', 'slack', 'api')),
      source_metadata TEXT,
      natural_language_description TEXT NOT NULL,
      status TEXT NOT NULL CHECK (status IN ('pending', 'analyzing', 'code_generated', 'committed', 'approved', 'rejected', 'failed')),
      priority TEXT NOT NULL CHECK (priority IN ('low', 'medium', 'high', 'urgent')),
      ai_analysis TEXT,
      generated_code TEXT,
      generated_tests TEXT,
      generated_documentation TEXT,
      commit_sha TEXT,
      commit_url TEXT,
      error_message TEXT,
      developer_notes TEXT,
      created_at TEXT NOT NULL DEFAULT (datetime('now')),
      updated_at TEXT NOT NULL DEFAULT (datetime('now')),
      completed_at TEXT
    );
  `);

  // Create feature_request_comments table
  db.exec(`
    CREATE TABLE IF NOT EXISTS feature_request_comments (
      id TEXT PRIMARY KEY,
      feature_request_id TEXT NOT NULL,
      author_name TEXT NOT NULL,
      author_email TEXT,
      comment_text TEXT NOT NULL,
      created_at TEXT NOT NULL DEFAULT (datetime('now')),
      FOREIGN KEY (feature_request_id) REFERENCES feature_requests(id) ON DELETE CASCADE
    );
  `);

  // Create feature_request_notifications table
  db.exec(`
    CREATE TABLE IF NOT EXISTS feature_request_notifications (
      id TEXT PRIMARY KEY,
      feature_request_id TEXT NOT NULL,
      recipient_email TEXT NOT NULL,
      notification_type TEXT NOT NULL CHECK (notification_type IN ('new_request', 'code_generated', 'committed', 'approved', 'rejected', 'failed')),
      sent_at TEXT NOT NULL DEFAULT (datetime('now')),
      delivery_status TEXT NOT NULL CHECK (delivery_status IN ('pending', 'sent', 'failed')),
      error_message TEXT,
      FOREIGN KEY (feature_request_id) REFERENCES feature_requests(id) ON DELETE CASCADE
    );
  `);

  // Create conversations table for Annette's memory
  db.exec(`
    CREATE TABLE IF NOT EXISTS conversations (
      id TEXT PRIMARY KEY,
      project_id TEXT NOT NULL,
      title TEXT,
      created_at TEXT NOT NULL DEFAULT (datetime('now')),
      updated_at TEXT NOT NULL DEFAULT (datetime('now'))
    );
  `);

  // Create messages table for conversation history
  db.exec(`
    CREATE TABLE IF NOT EXISTS messages (
      id TEXT PRIMARY KEY,
      conversation_id TEXT NOT NULL,
      role TEXT NOT NULL CHECK (role IN ('user', 'assistant', 'system')),
      content TEXT NOT NULL,
      memory_type TEXT, -- Free string for future categorization (e.g., 'user_preference', 'project_fact', 'action')
      metadata TEXT, -- JSON string for additional data
      created_at TEXT NOT NULL DEFAULT (datetime('now')),
      FOREIGN KEY (conversation_id) REFERENCES conversations(id) ON DELETE CASCADE
    );
  `);

  // Create tech_debt table for Technical Debt Radar
  db.exec(`
    CREATE TABLE IF NOT EXISTS tech_debt (
      id TEXT PRIMARY KEY,
      project_id TEXT NOT NULL,
      scan_id TEXT,
      category TEXT NOT NULL CHECK (category IN (
        'code_quality', 'security', 'performance', 'maintainability',
        'testing', 'documentation', 'dependencies', 'architecture',
        'accessibility', 'other'
      )),
      title TEXT NOT NULL,
      description TEXT NOT NULL,
      severity TEXT NOT NULL CHECK (severity IN ('critical', 'high', 'medium', 'low')),
      risk_score INTEGER NOT NULL CHECK (risk_score >= 0 AND risk_score <= 100),

      -- Impact metrics
      estimated_effort_hours REAL,
      impact_scope TEXT, -- JSON array
      technical_impact TEXT,
      business_impact TEXT,

      -- Detection information
      detected_by TEXT NOT NULL CHECK (detected_by IN ('automated_scan', 'manual_entry', 'ai_analysis')),
      detection_details TEXT, -- JSON metadata
      file_paths TEXT, -- JSON array

      -- Remediation planning
      status TEXT NOT NULL CHECK (status IN ('detected', 'acknowledged', 'planned', 'in_progress', 'resolved', 'dismissed')),
      remediation_plan TEXT, -- JSON structured plan
      remediation_steps TEXT, -- JSON array
      estimated_completion_date TEXT,

      -- Integration
      backlog_item_id TEXT,
      goal_id TEXT,

      -- Timestamps
      created_at TEXT NOT NULL DEFAULT (datetime('now')),
      updated_at TEXT NOT NULL DEFAULT (datetime('now')),
      resolved_at TEXT,
      dismissed_at TEXT,
      dismissal_reason TEXT,

      FOREIGN KEY (scan_id) REFERENCES scans(id) ON DELETE SET NULL,
      FOREIGN KEY (backlog_item_id) REFERENCES backlog_items(id) ON DELETE SET NULL,
      FOREIGN KEY (goal_id) REFERENCES goals(id) ON DELETE SET NULL
    );
  `);

  // Run migrations for existing databases
  runMigrations();

  // Create indexes for better query performance
  db.exec(`
    CREATE INDEX IF NOT EXISTS idx_goals_project_id ON goals(project_id);
    CREATE INDEX IF NOT EXISTS idx_goals_order_index ON goals(project_id, order_index);
    CREATE INDEX IF NOT EXISTS idx_backlog_items_project_id ON backlog_items(project_id);
    CREATE INDEX IF NOT EXISTS idx_backlog_items_goal_id ON backlog_items(goal_id);
    CREATE INDEX IF NOT EXISTS idx_backlog_items_status ON backlog_items(project_id, status);
    CREATE INDEX IF NOT EXISTS idx_context_groups_project_id ON context_groups(project_id);
    CREATE INDEX IF NOT EXISTS idx_context_groups_position ON context_groups(project_id, position);
    CREATE INDEX IF NOT EXISTS idx_contexts_project_id ON contexts(project_id);
    CREATE INDEX IF NOT EXISTS idx_contexts_group_id ON contexts(group_id);
    CREATE INDEX IF NOT EXISTS idx_events_project_id ON events(project_id);
    CREATE INDEX IF NOT EXISTS idx_events_created_at ON events(project_id, created_at);
    CREATE INDEX IF NOT EXISTS idx_events_type ON events(project_id, type);
    CREATE INDEX IF NOT EXISTS idx_scans_project_id ON scans(project_id);
    CREATE INDEX IF NOT EXISTS idx_scans_timestamp ON scans(project_id, timestamp);
    CREATE INDEX IF NOT EXISTS idx_ideas_scan_id ON ideas(scan_id);
    CREATE INDEX IF NOT EXISTS idx_ideas_project_id ON ideas(project_id);
    CREATE INDEX IF NOT EXISTS idx_ideas_context_id ON ideas(context_id);
    CREATE INDEX IF NOT EXISTS idx_ideas_status ON ideas(project_id, status);
    CREATE INDEX IF NOT EXISTS idx_ideas_category ON ideas(category);
    CREATE INDEX IF NOT EXISTS idx_implementation_log_project_id ON implementation_log(project_id);
    CREATE INDEX IF NOT EXISTS idx_implementation_log_created_at ON implementation_log(project_id, created_at);
    CREATE INDEX IF NOT EXISTS idx_feature_requests_project_id ON feature_requests(project_id);
    CREATE INDEX IF NOT EXISTS idx_feature_requests_status ON feature_requests(project_id, status);
    CREATE INDEX IF NOT EXISTS idx_feature_requests_created_at ON feature_requests(project_id, created_at);
    CREATE INDEX IF NOT EXISTS idx_feature_request_comments_request_id ON feature_request_comments(feature_request_id);
    CREATE INDEX IF NOT EXISTS idx_feature_request_notifications_request_id ON feature_request_notifications(feature_request_id);
    CREATE INDEX IF NOT EXISTS idx_feature_request_notifications_status ON feature_request_notifications(delivery_status);
    CREATE INDEX IF NOT EXISTS idx_tech_debt_project_id ON tech_debt(project_id);
    CREATE INDEX IF NOT EXISTS idx_tech_debt_status ON tech_debt(project_id, status);
    CREATE INDEX IF NOT EXISTS idx_tech_debt_severity ON tech_debt(project_id, severity);
    CREATE INDEX IF NOT EXISTS idx_tech_debt_category ON tech_debt(category);
    CREATE INDEX IF NOT EXISTS idx_tech_debt_risk_score ON tech_debt(project_id, risk_score);
    CREATE INDEX IF NOT EXISTS idx_tech_debt_backlog_item ON tech_debt(backlog_item_id);
  `);
}

'use client';

import React from 'react';
import { AnimatePresence } from 'framer-motion';
import { DbIdea } from '@/app/db';
import { Context } from '@/lib/queries/contextQueries';
import { useProjectConfigStore } from '@/stores/projectConfigStore';
import { useActiveProjectStore } from '@/stores/activeProjectStore';

// Components
import IdeasHeaderWithFilter from '@/app/features/Ideas/components/IdeasHeaderWithFilter';
import BufferView from '@/app/features/Ideas/sub_Buffer/BufferView';
import IdeaDetailModal from '@/app/features/Ideas/components/IdeaDetailModal';
import { ScanType } from '@/app/features/Ideas/sub_IdeasSetup/ScanTypeSelector';
import ScanInitiator from '@/app/features/Ideas/sub_IdeasSetup/ScanInitiator';
import LazyContentSection from '@/components/Navigation/LazyContentSection';

// Handlers and utilities
import { fetchIdeas, deleteIdea } from '@/app/features/Ideas/lib/ideasHandlers';
import {
  getProjectName,
  groupIdeasByProjectAndContext,
  calculateIdeaStats
} from '@/app/features/Ideas/lib/ideasUtils';
import {
  fetchContextsForProjects,
  getContextNameFromMap
} from '@/app/features/Ideas/lib/contextLoader';
import { ProcessingIdeaProvider } from '@/app/features/Ideas/lib/ProcessingIdeaContext';

export default function IdeasPage() {
  const [ideas, setIdeas] = React.useState<DbIdea[]>([]);
  const [loading, setLoading] = React.useState(true);
  const [selectedIdea, setSelectedIdea] = React.useState<DbIdea | null>(null);
  const [filterProject, setFilterProject] = React.useState<string>('all');
  const [filterContext, setFilterContext] = React.useState<string | null>(null);
  const [selectedScanTypes, setSelectedScanTypes] = React.useState<ScanType[]>([]);
  const [contextsMap, setContextsMap] = React.useState<Record<string, Context[]>>({});

  const { projects, initializeProjects, getProject } = useProjectConfigStore();
  const { setActiveProject } = useActiveProjectStore();

  // Initialize projects on mount
  React.useEffect(() => {
    initializeProjects();
  }, [initializeProjects]);

  // Fetch ideas on mount
  React.useEffect(() => {
    loadIdeas();
  }, []);

  // Load contexts for all projects when project IDs change (not on every idea update)
  React.useEffect(() => {
    const loadContextsForIdeas = async () => {
      // Get unique project IDs from ideas
      const projectIds = [...new Set(ideas.map(idea => idea.project_id))];

      if (projectIds.length > 0) {
        const contexts = await fetchContextsForProjects(projectIds);
        setContextsMap(contexts);
      }
    };

    if (ideas.length > 0) {
      loadContextsForIdeas();
    }
  }, [ideas.map(idea => idea.project_id).sort().join(',')]);

  const loadIdeas = async () => {
    setLoading(true);
    const fetchedIdeas = await fetchIdeas();
    setIdeas(fetchedIdeas);
    setLoading(false);
  };

  const handleIdeaUpdate = async (updatedIdea: DbIdea) => {
    setIdeas(ideas.map(idea => idea.id === updatedIdea.id ? updatedIdea : idea));
    setSelectedIdea(updatedIdea);
  };

  const handleIdeaDelete = async (deletedIdeaId: string) => {
    setIdeas(ideas.filter(idea => idea.id !== deletedIdeaId));
    setSelectedIdea(null);
  };

  const handleQuickDelete = async (ideaId: string) => {
    const success = await deleteIdea(ideaId);
    if (success) {
      setIdeas(ideas.filter(idea => idea.id !== ideaId));
    }
  };

  const handleContextDelete = async (contextId: string) => {
    // Capture ideas to delete BEFORE optimistic update
    const contextIdeas = ideas.filter(idea => idea.context_id === contextId);
    const projectId = contextIdeas[0]?.project_id;
    const project = projectId ? getProject(projectId) : null;

    // Optimistically update UI immediately
    setIdeas(prevIdeas => prevIdeas.filter(idea => idea.context_id !== contextId));

    try {
      const response = await fetch(
        `/api/contexts/ideas?contextId=${encodeURIComponent(contextId)}${project?.path ? `&projectPath=${encodeURIComponent(project.path)}` : ''}`,
        { method: 'DELETE' }
      );

      if (response.ok) {
        const data = await response.json();
        console.log(`Deleted ${data.deletedCount} idea(s) from context`);
      } else {
        const errorData = await response.json();
        console.error('Failed to delete context ideas:', errorData.error);
        // Revert optimistic update on error
        setIdeas(prevIdeas => [...prevIdeas, ...contextIdeas]);
        alert(`Failed to delete ideas: ${errorData.error}`);
      }
    } catch (error) {
      console.error('Error deleting context ideas:', error);
      // Revert optimistic update on error
      setIdeas(prevIdeas => [...prevIdeas, ...contextIdeas]);
      alert('Failed to delete ideas. Please refresh the page.');
    }
  };

  const handleIdeaClose = () => {
    setSelectedIdea(null);
  };

  const handleScanComplete = () => {
    loadIdeas();
  };

  const handleProjectSelect = (projectId: string) => {
    // Update local filter state
    setFilterProject(projectId);
    setFilterContext(null); // Reset context filter when project changes

    // Update active project in store (skip if 'all' is selected)
    if (projectId !== 'all') {
      const project = getProject(projectId);
      if (project) {
        setActiveProject(project);
      }
    }
  };

  // Get selected project details
  const selectedProject = filterProject !== 'all' ? getProject(filterProject) : null;

  // Filter ideas to only show Pending and Accepted
  const filteredIdeas = React.useMemo(() =>
    ideas.filter(idea => idea.status === 'pending' || idea.status === 'accepted'),
    [ideas]
  );

  // Compute grouped ideas and stats
  const groupedIdeas = React.useMemo(() =>
    groupIdeasByProjectAndContext(filteredIdeas, 'all', filterProject),
    [filteredIdeas, filterProject]
  );

  const stats = React.useMemo(() => calculateIdeaStats(ideas), [ideas]);

  // Helper function to get context name using the loaded contexts map
  const getContextName = React.useCallback((contextId: string) => {
    return getContextNameFromMap(contextId, contextsMap);
  }, [contextsMap]);

  return (
    <ProcessingIdeaProvider>
      <div className="min-h-full bg-gradient-to-br from-gray-900 via-gray-900 to-gray-800">
        {/* Header with Project Filter */}
        <LazyContentSection delay={0}>
          <IdeasHeaderWithFilter
            projects={projects}
            selectedProjectId={filterProject}
            onSelectProject={handleProjectSelect}
            selectedContextId={filterContext}
            onSelectContext={setFilterContext}
            stats={stats}
            selectedProjectPath={selectedProject?.path}
            onIdeaImplemented={loadIdeas}
          />
        </LazyContentSection>

        {/* Scan Initiator - Now includes scan type selector inline */}
        <LazyContentSection delay={0.1}>
          <div className="w-full px-6 py-4">
            <ScanInitiator
              onScanComplete={handleScanComplete}
              selectedScanTypes={selectedScanTypes}
              onScanTypesChange={setSelectedScanTypes}
              selectedContextId={filterContext}
            />
          </div>
        </LazyContentSection>

        {/* Content */}
        <LazyContentSection delay={0.2}>
          <div className="w-full px-6 py-8">
            {/* Ideas Display */}
            <BufferView
              loading={loading}
              ideas={filteredIdeas}
              groupedIdeas={groupedIdeas}
              getProjectName={(id) => getProjectName(id, projects)}
              getContextName={getContextName}
              onIdeaClick={setSelectedIdea}
              onIdeaDelete={handleQuickDelete}
              onContextDelete={handleContextDelete}
            />
          </div>
        </LazyContentSection>

        {/* Detail Modal */}
        <AnimatePresence>
          {selectedIdea && (
            <IdeaDetailModal
              idea={selectedIdea}
              onClose={handleIdeaClose}
              onUpdate={handleIdeaUpdate}
              onDelete={handleIdeaDelete}
            />
          )}
        </AnimatePresence>
      </div>
    </ProcessingIdeaProvider>
  );
}

