}: HighLevelDocsModalWrapperProps) {
  const { activeProject } = useActiveProjectStore();
  const provider = DefaultProviderStorage.getDefaultProvider();

  if (!activeProject) {
    return null;
  }

  return (
    <HighLevelDocsModal
      isOpen={isOpen}
      onClose={onClose}
      projectId={activeProject.id}
      projectName={activeProject.name}
      projectPath={activeProject.path}
      provider={provider}
    />
  );
}

'use client';
import React, { useState } from 'react';
import { motion } from 'framer-motion';
import {
  Target,
  Zap,
  Trash2,
} from 'lucide-react';
import { useAnalysisStore } from '../../stores/analysisStore';
import { useActiveProjectStore } from '../../stores/activeProjectStore';
import { useProjectsToolbarStore } from '../../stores/projectsToolbarStore';
import { useProjectConfigStore } from '../../stores/projectConfigStore';
import { deleteProject } from './ProjectSetting/lib/projectApi';
import HighLevelDocsModalWrapper from './HighLevelDocsModalWrapper';
import ProjectAdd from './ProjectSetting/ProjectAdd';
import ProjectEdit from './ProjectSetting/ProjectEdit';
import ProjectManagement from './ProjectSetting/ProjectManagement';
import ReviewerPanel from '../reviewer/ReviewerPanel';
import CodeReviewModal from '../reviewer/CodeReviewModal';
import { ActionGroup, ActionConfig } from '@/components/ui';
import GlowWrapper from '@/app/features/Onboarding/components/GlowWrapper';
import { useActiveOnboardingStep } from '@/app/features/Onboarding/lib/useOnboardingConditions';


export default function ProjectsLayout() {
  const { isActive } = useAnalysisStore();
  const { activeProject, setActiveProject } = useActiveProjectStore();
  const { syncWithServer } = useProjectConfigStore();

  // Onboarding
  const { isGenerateDocsActive } = useActiveOnboardingStep();

  const {
    showAddProject,
    showEditProject,
    showAIReview,
    setShowAddProject,
    setShowEditProject,
    setShowAddGoal,
    setShowAIReview,
  } = useProjectsToolbarStore();

  // Code Review Modal state - managed at layout level for proper z-index
  const [showCodeReviewModal, setShowCodeReviewModal] = useState(false);

  // Handle code review completion
  const handleCodeReviewComplete = () => {
    setShowCodeReviewModal(false);
    // The ReviewerPanel will auto-refresh its count via useEffect
  };


  // Goals Management Handlers
  const handleAddGoal = () => {
    setShowAddGoal(true);
  };

  // AI Features Handlers
  const handleAIProjectReview = () => {
    setShowAIReview(true);
  };

  // Handle project added - refresh the project list
  const handleProjectAdded = async () => {
    // Only sync for additions, as they need to fetch the full project data
    await syncWithServer();
  };

  // Handle project updated - no need to refresh, optimistic updates handle it
  const handleProjectUpdated = () => {
    // Stores are already updated optimistically, no need to fetch
    console.log('[ProjectsLayout] Project updated via optimistic update');
  };

  // Handle project deletion
  const handleDeleteProject = async () => {
    if (!activeProject) return;

    const confirmDelete = window.confirm(
      `Are you sure you want to delete project "${activeProject.name}"? This action cannot be undone.`
    );

    if (confirmDelete) {
      const success = await deleteProject(activeProject.id);
      if (success) {
        // Refresh the project list and let the store handle clearing active project
        await syncWithServer();
        // Check if there are other projects available
        const { getAllProjects } = useProjectConfigStore.getState();
        const remainingProjects = getAllProjects();
        if (remainingProjects.length > 0) {
          // Set the first available project as active
          setActiveProject(remainingProjects[0]);
        }
      } else {
        alert('Failed to delete project. Please try again.');
      }
    }
  };

  // Define actions for ActionGroup
  const projectActions: ActionConfig[] = [
    {
      id: 'add-goal',
      icon: Target,
      text: 'Goal',
      onClick: handleAddGoal,
      tooltip: 'Add new goal',
      colorScheme: 'blue',
      iconAnimation: 'rotate',
    },
    {
      id: 'ai-review',
      icon: Zap,
      text: 'Plan',
      onClick: handleAIProjectReview,
      tooltip: 'AI-powered project review and analysis',
      disabled: !activeProject,
      colorScheme: 'amber',
      iconAnimation: 'scale',
    },
    {
      id: 'delete-project',
      icon: Trash2,
      text: '',
      onClick: handleDeleteProject,
      tooltip: 'Delete current project',
      disabled: !activeProject,
      colorScheme: 'red',
      iconOnly: true,
      iconAnimation: 'scale',
    },
  ];

  return (
    <>
      <motion.div
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
        className={`w-full py-4 bg-gradient-to-r from-gray-900/95 via-gray-800/95 to-gray-900/95 backdrop-blur-xl border-b border-gray-700/30 ${isActive ? 'shadow-lg shadow-blue-500/20' : ''
          }`}
      >
        <div className="h-full w-full flex items-center justify-between px-6 gap-8">

          {/* Left Section: Project Management */}
          <ProjectManagement />

          {/* Middle Section: Reviewer Panel */}
          <ReviewerPanel onOpenReview={() => setShowCodeReviewModal(true)} />

          {/* Right Section: Goals & AI Actions */}
          <div className="flex items-center space-x-4">
            <GlowWrapper isActive={isGenerateDocsActive}>
              <ActionGroup label="Actions" actions={projectActions} />
            </GlowWrapper>
          </div>
        </div>
      </motion.div>

      {/* High-Level Documentation Modal */}
      <HighLevelDocsModalWrapper
        isOpen={showAIReview}
        onClose={() => setShowAIReview(false)}
      />

      {/* Add Project Modal */}
      <ProjectAdd
        isOpen={showAddProject}
        onClose={() => setShowAddProject(false)}
        onProjectAdded={handleProjectAdded}
      />

      {/* Edit Project Modal */}
      <ProjectEdit
        isOpen={showEditProject}
        onClose={() => setShowEditProject(false)}
        onProjectUpdated={handleProjectUpdated}
        project={activeProject}
      />

      {/* Code Review Modal - Rendered at layout level for proper z-index */}
      {showCodeReviewModal && (
        <CodeReviewModal
          isOpen={showCodeReviewModal}
          onClose={() => setShowCodeReviewModal(false)}
          onComplete={handleCodeReviewComplete}
          projectId={activeProject?.id || ''}
        />
      )}
    </>
  );
}

'use client';

import React, { useState, useEffect } from 'react';
import { useSearchParams, useRouter } from 'next/navigation';
import { motion } from 'framer-motion';
import { Trophy, Calendar, Network, BarChart3 } from 'lucide-react';
import { DbIdea } from '@/app/db';
import { useProjectConfigStore } from '@/stores/projectConfigStore';
import { useContextStore } from '@/stores/contextStore';
import TotalViewFilters from '@/app/features/reflector/components/TotalViewFilters';
import TotalViewDashboard from '@/app/features/reflector/components/TotalViewDashboard';
import ActiveFiltersDisplay from '@/app/features/reflector/components/ActiveFiltersDisplay';
import DependenciesTab from '@/app/features/Depndencies/DependenciesTab';
import ReflectionDashboard from '@/app/features/reflector/sub_Reflection/components/ReflectionDashboard';
import { IdeaFilterState, getEmptyFilterState, applyFilters } from '@/app/features/reflector/lib/filterIdeas';

export default function ReflectorPage() {
  const [ideas, setIdeas] = useState<DbIdea[]>([]);
  const [loading, setLoading] = useState(true);
  const [viewMode, setViewMode] = useState<'weekly' | 'total' | 'ideas_stats' | 'dependencies'>('weekly');
  const [filters, setFilters] = useState<IdeaFilterState>(getEmptyFilterState());

  const { projects, initializeProjects } = useProjectConfigStore();
  const searchParams = useSearchParams();
  const router = useRouter();

  // Initialize projects and load ideas
  useEffect(() => {
    initializeProjects();
    loadImplementedIdeas();
  }, []);

  // Sync filters with URL parameters
  useEffect(() => {
    const projectIds = searchParams.get('projects')?.split(',').filter(Boolean) || [];
    const contextIds = searchParams.get('contexts')?.split(',').filter(Boolean) || [];
    const statuses = searchParams.get('statuses')?.split(',').filter(Boolean) || [];
    const startDate = searchParams.get('startDate');
    const endDate = searchParams.get('endDate');
    const searchQuery = searchParams.get('search') || '';

    setFilters({
      projectIds,
      contextIds,
      statuses,
      dateRange: {
        start: startDate ? new Date(startDate) : null,
        end: endDate ? new Date(endDate) : null,
      },
      searchQuery,
    });
  }, [searchParams]);

  const loadImplementedIdeas = async () => {
    try {
      const response = await fetch('/api/ideas?status=implemented');
      if (response.ok) {
        const data = await response.json();
        setIdeas(data.ideas || []);
      }
    } catch (error) {
      console.error('Error loading implemented ideas:', error);
    } finally {
      setLoading(false);
    }
  };

  // Apply filters and view mode
  const displayedIdeas = React.useMemo(() => {
    let filtered = ideas;

    // Apply view mode filter first (weekly vs total)
    if (viewMode === 'weekly') {
      const oneWeekAgo = new Date();
      oneWeekAgo.setDate(oneWeekAgo.getDate() - 7);

      filtered = filtered.filter(idea => {
        if (!idea.implemented_at) return false;
        const implementedDate = new Date(idea.implemented_at);
        return implementedDate >= oneWeekAgo;
      });
    }

    // Apply user filters only in total view
    if (viewMode === 'total') {
      filtered = applyFilters(filtered, filters);
    }

    return filtered;
  }, [ideas, viewMode, filters]);

  // Calculate stats
  const stats = React.useMemo(() => {
    const now = new Date();
    const today = new Date(now.getFullYear(), now.getMonth(), now.getDate());
    const weekAgo = new Date(today);
    weekAgo.setDate(weekAgo.getDate() - 7);
    const monthAgo = new Date(today);
    monthAgo.setMonth(monthAgo.getMonth() - 1);

    return {
      today: ideas.filter(i => {
        if (!i.implemented_at) return false;
        const date = new Date(i.implemented_at);
        return date >= today;
      }).length,
      week: ideas.filter(i => {
        if (!i.implemented_at) return false;
        const date = new Date(i.implemented_at);
        return date >= weekAgo;
      }).length,
      month: ideas.filter(i => {
        if (!i.implemented_at) return false;
        const date = new Date(i.implemented_at);
        return date >= monthAgo;
      }).length,
    };
  }, [ideas]);

  // Update URL when filters change
  const handleFilterChange = (newFilters: IdeaFilterState) => {
    setFilters(newFilters);

    const params = new URLSearchParams();
    if (newFilters.projectIds.length > 0) {
      params.set('projects', newFilters.projectIds.join(','));
    }
    if (newFilters.contextIds.length > 0) {
      params.set('contexts', newFilters.contextIds.join(','));
    }
    if (newFilters.statuses.length > 0) {
      params.set('statuses', newFilters.statuses.join(','));
    }
    if (newFilters.dateRange.start) {
      params.set('startDate', newFilters.dateRange.start.toISOString());
    }
    if (newFilters.dateRange.end) {
      params.set('endDate', newFilters.dateRange.end.toISOString());
    }
    if (newFilters.searchQuery.trim()) {
      params.set('search', newFilters.searchQuery);
    }

    const newUrl = params.toString() ? `?${params.toString()}` : '/reflector';
    router.push(newUrl, { scroll: false });
  };

  // Remove individual filter
  const handleRemoveFilter = (filterType: keyof IdeaFilterState, value?: string) => {
    const newFilters = { ...filters };

    if (filterType === 'projectIds' && value) {
      newFilters.projectIds = newFilters.projectIds.filter(id => id !== value);
    } else if (filterType === 'contextIds' && value) {
      newFilters.contextIds = newFilters.contextIds.filter(id => id !== value);
    } else if (filterType === 'statuses' && value) {
      newFilters.statuses = newFilters.statuses.filter(s => s !== value);
    } else if (filterType === 'dateRange') {
      newFilters.dateRange = { start: null, end: null };
    } else if (filterType === 'searchQuery') {
      newFilters.searchQuery = '';
    }

    handleFilterChange(newFilters);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 via-yellow-900/10 to-gray-900">
      {/* Header */}
      <motion.div
        className="border-b border-yellow-700/40 bg-gray-900/60 backdrop-blur-xl"
        initial={{ y: -20, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        transition={{ duration: 0.5 }}
      >
        <div className="max-w-7xl mx-auto px-6 py-6">
          <div className="flex items-center justify-between mb-4">
            {/* Left: Title */}
            <div className="flex items-center space-x-4">
              <motion.div
                className="p-3 bg-gradient-to-br from-yellow-500/20 to-amber-500/30 rounded-xl border border-yellow-500/40"
                animate={{ rotate: [0, 5, -5, 0] }}
                transition={{ duration: 4, repeat: Infinity }}
              >
                <Trophy className="w-6 h-6 text-yellow-400" />
              </motion.div>
              <div>
                <h1 className="text-2xl font-bold bg-gradient-to-r from-yellow-400 to-amber-400 bg-clip-text text-transparent">
                  Reflector
                </h1>
                <p className="text-sm text-gray-400 mt-1">
                  Implemented ideas and achievements
                </p>
              </div>
            </div>

            {/* Right: Stats */}
            <div className="flex items-center space-x-6">
              <div className="flex items-center gap-2">
                <Calendar className="w-4 h-4 text-yellow-400" />
                <div className="text-sm">
                  <span className="text-gray-500">Today:</span>{' '}
                  <span className="text-yellow-400 font-mono font-semibold">{stats.today}</span>
                </div>
              </div>
              <div className="text-sm">
                <span className="text-gray-500">Week:</span>{' '}
                <span className="text-yellow-400 font-mono font-semibold">{stats.week}</span>
              </div>
              <div className="text-sm">
                <span className="text-gray-500">Month:</span>{' '}
                <span className="text-amber-400 font-mono font-semibold">{stats.month}</span>
              </div>
            </div>
          </div>

          {/* View Mode Tabs */}
          <div className="flex items-center gap-2">
            <button
              onClick={() => setViewMode('weekly')}
              className={`px-4 py-2 rounded-lg text-sm font-semibold transition-all ${
                viewMode === 'weekly'
                  ? 'bg-yellow-500/20 text-yellow-300 border border-yellow-500/40'
                  : 'bg-gray-800/40 text-gray-400 border border-gray-700/40 hover:bg-gray-800/60'
              }`}
            >
              Weekly
            </button>
            <button
              onClick={() => setViewMode('total')}
              className={`px-4 py-2 rounded-lg text-sm font-semibold transition-all ${
                viewMode === 'total'
                  ? 'bg-yellow-500/20 text-yellow-300 border border-yellow-500/40'
                  : 'bg-gray-800/40 text-gray-400 border border-gray-700/40 hover:bg-gray-800/60'
              }`}
            >
              Implemented
            </button>
            <button
              onClick={() => setViewMode('ideas_stats')}
              className={`flex items-center gap-2 px-4 py-2 rounded-lg text-sm font-semibold transition-all ${
                viewMode === 'ideas_stats'
                  ? 'bg-yellow-500/20 text-yellow-300 border border-yellow-500/40'
                  : 'bg-gray-800/40 text-gray-400 border border-gray-700/40 hover:bg-gray-800/60'
              }`}
            >
              <BarChart3 className="w-4 h-4" />
              Ideas Stats
            </button>
            <button
              onClick={() => setViewMode('dependencies')}
              className={`flex items-center gap-2 px-4 py-2 rounded-lg text-sm font-semibold transition-all ${
                viewMode === 'dependencies'
                  ? 'bg-yellow-500/20 text-yellow-300 border border-yellow-500/40'
                  : 'bg-gray-800/40 text-gray-400 border border-gray-700/40 hover:bg-gray-800/60'
              }`}
            >
              <Network className="w-4 h-4" />
              Dependencies
            </button>
          </div>
        </div>
      </motion.div>

      {/* Content */}
      <div className="max-w-7xl mx-auto px-6 py-8">
        {loading && viewMode !== 'dependencies' && viewMode !== 'ideas_stats' ? (
          <div className="flex items-center justify-center py-24">
            <div className="text-gray-400">Loading...</div>
          </div>
        ) : viewMode === 'weekly' ? (
          <div className="text-center py-24 text-gray-400">
            Weekly view content will be implemented here
            <br />
            {displayedIdeas.length} ideas implemented in the last 7 days
          </div>
        ) : viewMode === 'dependencies' ? (
          <DependenciesTab />
        ) : viewMode === 'ideas_stats' ? (
          <ReflectionDashboard />
        ) : (
          <div className="space-y-6">
            {/* Filters and Active Filter Display */}
            <div className="space-y-4">
              <TotalViewFilters
                projects={projects}
                filters={filters}
                onChange={handleFilterChange}
                ideas={ideas}
              />
              <ActiveFiltersDisplay
                filters={filters}
                projects={projects}
                onRemoveFilter={handleRemoveFilter}
              />
            </div>

            {/* Dashboard */}
            <TotalViewDashboard
              ideas={displayedIdeas}
              isFiltered={
                filters.projectIds.length > 0 ||
                filters.contextIds.length > 0 ||
                filters.statuses.length > 0 ||
                !!filters.dateRange.start ||
                !!filters.dateRange.end ||
                !!filters.searchQuery.trim()
              }
            />
          </div>
        )}
      </div>
    </div>
  );
}

import React from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { MonacoDiffEditor, MonacoEditor } from '@/components/editor';
import { ReviewFile, getLanguageFromFilepath } from './lib';

interface CodeReviewEditorProps {
  currentFile: ReviewFile | undefined;
  onContentChange: (content: string) => void;
}

export default function CodeReviewEditor({ currentFile, onContentChange }: CodeReviewEditorProps) {
  if (!currentFile) {
    return (
      <div className="h-[70vh] flex items-center justify-center">
        <div className="text-gray-400">No file selected</div>
      </div>
    );
  }

  return (
    <div className="h-[70vh] overflow-hidden">
      <AnimatePresence mode="wait">
        <motion.div
          key={`${currentFile.id}-${currentFile.isEditing ? 'edit' : 'diff'}`}
          initial={{ opacity: 0, x: 20 }}
          animate={{ opacity: 1, x: 0 }}
          exit={{ opacity: 0, x: -20 }}
          transition={{ duration: 0.3 }}
          className="h-full"
        >
          {currentFile.isEditing ? (
            // Edit Mode - Monaco Editor
            <div className="h-full border border-gray-700/30 rounded-lg overflow-hidden">
              <MonacoEditor
                value={currentFile.editedContent || currentFile.generated_content}
                onChange={onContentChange}
                language={getLanguageFromFilepath(currentFile.filepath)}
                theme="vs-dark"
                height="100%"
                width="100%"
                options={{
                  minimap: { enabled: true },
                  scrollBeyondLastLine: false,
                  fontSize: 14,
                  lineNumbers: 'on',
                  wordWrap: 'on',
                  automaticLayout: true
                }}
              />
            </div>
          ) : (
            // Diff Mode - Monaco Diff Editor
            <div className="h-full border border-gray-700/30 rounded-lg overflow-hidden">
              <MonacoDiffEditor
                original={currentFile.original_content || ''}
                modified={currentFile.generated_content}
                language={getLanguageFromFilepath(currentFile.filepath)}
                theme="vs-dark"
                height="100%"
                width="100%"
                options={{
                  minimap: { enabled: true },
                  scrollBeyondLastLine: false,
                  fontSize: 14,
                  renderSideBySide: true,
                  readOnly: true,
                  automaticLayout: true
                }}
              />
            </div>
          )}
        </motion.div>
      </AnimatePresence>
    </div>
  );
}

import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { Plus, Edit3 } from 'lucide-react';
import CodeReviewNav from './CodeReviewNav';
import CodeReviewEditor from './CodeReviewEditor';
import {
  ReviewSession,
  fetchPendingSessions,
  calculateTotalFiles,
  calculateGlobalFileIndex,
  toggleFileEditing,
  updateFileContent,
  getFileActionLabel
} from './lib';

interface CodeReviewModalProps {
  isOpen: boolean;
  onClose: () => void;
  onComplete: () => void;
  projectId: string;
}

export default function CodeReviewModal({ isOpen, onClose, onComplete, projectId }: CodeReviewModalProps) {
  const [sessions, setSessions] = useState<ReviewSession[]>([]);
  const [currentSessionIndex, setCurrentSessionIndex] = useState(0);
  const [currentFileIndex, setCurrentFileIndex] = useState(0);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  // Fetch pending files grouped by sessions
  const fetchPendingFiles = async () => {
    if (!projectId || !isOpen) return;

    setLoading(true);
    setError(null);
    try {
      const data = await fetchPendingSessions(projectId);
      setSessions(data.sessions || []);
      setCurrentSessionIndex(0);
      setCurrentFileIndex(0);
    } catch (err) {
      setError('Error loading files');
      console.error('Failed to fetch pending files:', err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchPendingFiles();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [isOpen, projectId]);

  const currentSession = sessions[currentSessionIndex];
  const currentFile = currentSession?.files[currentFileIndex];
  const totalFiles = calculateTotalFiles(sessions);
  const currentFileGlobalIndex = calculateGlobalFileIndex(sessions, currentSessionIndex, currentFileIndex);

  // Handle sessions update from child components
  const handleSessionsUpdate = (updatedSessions: ReviewSession[]) => {
    setSessions(updatedSessions);
  };

  // Handle error from child components
  const handleError = (errorMessage: string) => {
    setError(errorMessage);
  };



  // Toggle edit mode
  const handleToggleEdit = () => {
    if (!currentFile) return;

    const updatedSessions = toggleFileEditing(sessions, currentSessionIndex, currentFileIndex);
    setSessions(updatedSessions);
  };

  // Handle content change in editor
  const handleContentChange = (content: string) => {
    if (!currentFile) return;

    const updatedSessions = updateFileContent(sessions, currentSessionIndex, currentFileIndex, content);
    setSessions(updatedSessions);
  };

  // Navigation
  const goToPrevious = () => {
    if (currentFileIndex > 0) {
      setCurrentFileIndex(currentFileIndex - 1);
    } else if (currentSessionIndex > 0) {
      setCurrentSessionIndex(currentSessionIndex - 1);
      setCurrentFileIndex(sessions[currentSessionIndex - 1].files.length - 1);
    }
  };

  const goToNext = () => {
    if (currentFileIndex < currentSession.files.length - 1) {
      setCurrentFileIndex(currentFileIndex + 1);
    } else if (currentSessionIndex < sessions.length - 1) {
      setCurrentSessionIndex(currentSessionIndex + 1);
      setCurrentFileIndex(0);
    }
  };

  if (loading) {
    return (
      <div className="fixed inset-0 z-[99999] bg-black/50 backdrop-blur-sm flex items-center justify-center">
        <div className="bg-slate-900 rounded-lg p-8 border border-slate-700">
          <div className="text-gray-400">Loading files for review...</div>
        </div>
      </div>
    );
  }

  if (totalFiles === 0) {
    return (
      <div className="fixed inset-0 z-[99999] bg-black/50 backdrop-blur-sm flex items-center justify-center">
        <div className="bg-slate-900 rounded-lg p-8 border border-slate-700">
          <div className="text-gray-400">No pending files found for review.</div>
        </div>
      </div>
    );
  }

  return (
    <div className="fixed inset-0 z-[99999] bg-black/50 backdrop-blur-sm">
      <div className="h-full flex flex-col bg-slate-900 border-l border-slate-700">
        {/* Header */}
        <div className="flex-shrink-0 p-4 border-b border-slate-700 bg-slate-800/50">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-3">
              <div className="p-2 bg-gradient-to-br from-cyan-800/60 to-blue-900/60 rounded-lg border border-slate-600/30">
                {currentFile?.action === 'create' ? (
                  <Plus className="w-5 h-5 text-cyan-300" />
                ) : (
                  <Edit3 className="w-5 h-5 text-cyan-300" />
                )}
              </div>
              <div>
                <h2 className="text-lg font-semibold text-white">
                  Code Review - {currentFile?.filepath || 'Loading...'}
                </h2>
                <p className="text-sm text-slate-400">
                  {currentSession?.taskTitle || 'Task'} â¢ File {currentFileGlobalIndex + 1} of {totalFiles} â¢ {getFileActionLabel(currentFile?.action || 'create')}
                </p>
              </div>
            </div>
            <button
              onClick={onClose}
              className="p-2 hover:bg-slate-700 rounded-lg transition-colors text-slate-400 hover:text-slate-300"
            >
              â
            </button>
          </div>
        </div>

        {/* Error Display */}
        {error && (
          <motion.div
            initial={{ opacity: 0, y: -10 }}
            animate={{ opacity: 1, y: 0 }}
            className="flex-shrink-0 m-4 p-3 bg-red-500/10 border border-red-500/30 rounded-lg text-red-400 text-sm"
          >
            {error}
          </motion.div>
        )}

        {/* Navigation */}
        <div className="flex-shrink-0">
          <CodeReviewNav
            sessions={sessions}
            currentSessionIndex={currentSessionIndex}
            currentFileIndex={currentFileIndex}
            currentFile={currentFile}
            onPrevious={goToPrevious}
            onNext={goToNext}
            onToggleEdit={handleToggleEdit}
            onSessionsUpdate={handleSessionsUpdate}
            onComplete={onComplete}
            onError={handleError}
            setCurrentSessionIndex={setCurrentSessionIndex}
            setCurrentFileIndex={setCurrentFileIndex}
          />
        </div>

        {/* Editor - Fixed height for proper visibility */}
        <div className="p-4">
          <CodeReviewEditor
            currentFile={currentFile}
            onContentChange={handleContentChange}
          />
        </div>
      </div>
    </div>
  );
}

import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { ChevronLeft, ChevronRight, Edit3, XIcon, X, Check } from 'lucide-react';
import {
  ReviewFile,
  ReviewSession,
  acceptFile,
  rejectFile,
  declineSession,
  calculateTotalFiles,
  calculateGlobalFileIndex,
  isFirstFile,
  isLastFile,
  removeFileFromSessions,
  getContentToApply,
  calculateProgress
} from './lib';

interface CodeReviewNavProps {
  sessions: ReviewSession[];
  currentSessionIndex: number;
  currentFileIndex: number;
  currentFile: ReviewFile | undefined;
  onPrevious: () => void;
  onNext: () => void;
  onToggleEdit: () => void;
  onSessionsUpdate: (sessions: ReviewSession[]) => void;
  onComplete: () => void;
  onError: (error: string) => void;
  setCurrentSessionIndex: (index: number) => void;
  setCurrentFileIndex: (index: number) => void;
}

export default function CodeReviewNav({
  sessions,
  currentSessionIndex,
  currentFileIndex,
  currentFile,
  onPrevious,
  onNext,
  onToggleEdit,
  onSessionsUpdate,
  onComplete,
  onError,
  setCurrentSessionIndex,
  setCurrentFileIndex
}: CodeReviewNavProps) {
  const [processing, setProcessing] = useState(false);

  // Handle file acceptance
  const handleAcceptFile = async () => {
    if (!currentFile) return;

    setProcessing(true);
    try {
      const contentToApply = getContentToApply(currentFile);

      await acceptFile({
        fileId: currentFile.id,
        content: contentToApply
      });

      // Remove accepted file from current session
      const result = removeFileFromSessions(sessions, currentSessionIndex, currentFileIndex);

      if (result.allSessionsEmpty) {
        onComplete();
        return;
      }

      setCurrentSessionIndex(result.newSessionIndex);
      setCurrentFileIndex(result.newFileIndex);
      onSessionsUpdate(result.updatedSessions);
    } catch (err) {
      onError('Error accepting file');
      console.error('Failed to accept file:', err);
    } finally {
      setProcessing(false);
    }
  };

  // Handle file rejection
  const handleRejectFile = async () => {
    if (!currentFile) return;

    setProcessing(true);
    try {
      await rejectFile({ fileId: currentFile.id });

      // Remove rejected file from current session
      const result = removeFileFromSessions(sessions, currentSessionIndex, currentFileIndex);

      if (result.allSessionsEmpty) {
        onComplete();
        return;
      }

      setCurrentSessionIndex(result.newSessionIndex);
      setCurrentFileIndex(result.newFileIndex);
      onSessionsUpdate(result.updatedSessions);
    } catch (err) {
      onError('Error rejecting file');
      console.error('Failed to reject file:', err);
    } finally {
      setProcessing(false);
    }
  };

  // Handle decline all (reject entire session)
  const handleDeclineAll = async () => {
    if (!currentFile) return;

    setProcessing(true);
    try {
      await declineSession({ sessionId: currentFile.session_id });
      onComplete();
    } catch (err) {
      onError('Error declining session');
      console.error('Failed to decline session:', err);
    } finally {
      setProcessing(false);
    }
  };
  const currentSession = sessions[currentSessionIndex];
  const totalSessions = sessions.length;
  const totalFiles = calculateTotalFiles(sessions);
  const currentFileGlobalIndex = calculateGlobalFileIndex(sessions, currentSessionIndex, currentFileIndex);

  const isFirst = isFirstFile(currentSessionIndex, currentFileIndex);
  const isLast = isLastFile(sessions, currentSessionIndex, currentFileIndex);

  return (
    <div className="flex items-center justify-between p-4 border-b border-gray-700/30 bg-slate-800/20">
      {/* Navigation */}
      <div className="flex items-center space-x-4">
        <motion.button
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
          onClick={onPrevious}
          disabled={isFirst}
          className="p-2 bg-gray-700/30 hover:bg-gray-700/50 border border-gray-600/30 rounded-lg text-gray-400 hover:text-gray-300 transition-all disabled:opacity-50 disabled:cursor-not-allowed"
        >
          <ChevronLeft className="w-4 h-4" />
        </motion.button>

        <div className="text-sm text-gray-400 text-center">
          <div className="font-medium">
            {currentFileGlobalIndex + 1} / {totalFiles}
          </div>
          <div className="text-sm text-gray-500">
            Session {currentSessionIndex + 1}/{totalSessions} â¢ File {currentFileIndex + 1}/{currentSession?.files.length || 0}
          </div>
        </div>

        <motion.button
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
          onClick={onNext}
          disabled={isLast}
          className="p-2 bg-gray-700/30 hover:bg-gray-700/50 border border-gray-600/30 rounded-lg text-gray-400 hover:text-gray-300 transition-all disabled:opacity-50 disabled:cursor-not-allowed"
        >
          <ChevronRight className="w-4 h-4" />
        </motion.button>
      </div>

      {/* Actions */}
      <div className="flex items-center space-x-3">
        <motion.button
          whileHover={{ scale: 1.02 }}
          whileTap={{ scale: 0.98 }}
          onClick={onToggleEdit}
          className="px-4 py-2 bg-blue-600/20 hover:bg-blue-600/30 border border-blue-600/30 text-blue-400 rounded-lg transition-colors font-medium flex items-center space-x-2"
        >
          <Edit3 className="w-4 h-4" />
          <span>{currentFile?.isEditing ? 'View Diff' : 'Edit'}</span>
        </motion.button>

        <motion.button
          whileHover={{ scale: 1.02 }}
          whileTap={{ scale: 0.98 }}
          onClick={handleDeclineAll}
          disabled={processing}
          className="px-4 py-2 bg-gray-600/20 hover:bg-gray-600/30 border border-gray-600/30 text-gray-400 rounded-lg transition-colors font-medium flex items-center space-x-2"
        >
          <XIcon className="w-4 h-4" />
          <span>Decline All</span>
        </motion.button>

        <motion.button
          whileHover={{ scale: 1.02 }}
          whileTap={{ scale: 0.98 }}
          onClick={handleRejectFile}
          disabled={processing}
          className="px-4 py-2 bg-red-600/20 hover:bg-red-600/30 border border-red-600/30 text-red-400 rounded-lg transition-colors font-medium flex items-center space-x-2"
        >
          <X className="w-4 h-4" />
          <span>Reject</span>
        </motion.button>

        <motion.button
          whileHover={{ scale: 1.02 }}
          whileTap={{ scale: 0.98 }}
          onClick={handleAcceptFile}
          disabled={processing}
          className="px-4 py-2 bg-green-600/20 hover:bg-green-600/30 border border-green-600/30 text-green-400 rounded-lg transition-colors font-medium flex items-center space-x-2"
        >
          <Check className="w-4 h-4" />
          <span>Accept</span>
        </motion.button>
      </div>

      {/* Progress Indicator */}
      <div className="absolute bottom-0 left-0 right-0 h-1 bg-gray-700/30">
        <motion.div
          className="h-full bg-gradient-to-r from-cyan-500 to-blue-500"
          initial={{ width: 0 }}
          animate={{ width: `${calculateProgress(currentFileGlobalIndex, totalFiles)}%` }}
          transition={{ duration: 0.5 }}
        />
      </div>
    </div>
  );
}

